mapper.delete:
delete
from faq
where faq_seq = #{faq_seq} and reg_id = #{admin_id}

mapper.update:
update faq
set faq_catg_code = #{faq_catg_code}, title = #{title}, cont = #{cont}, dsply_chk = #{dsply_chk}, up_date = now(), up_id = #{up_id}
where faq_seq = #{faq_seq}

update faq
set view_cnt = view_cnt + 1
where faq_seq = #{faq_seq}
