mapper.select:
SELECT ord_prod_seq, ord_seq, isbn, prod_type_code, ord_stat, deli_stat, pay_stat, cust_id, book_title, item_quan, point_perc, point_pric, basic_pric, disc_perc, disc_pric, sale_pric, ord_pric, repre_img, reg_date, reg_id, up_date, up_id, updated_at, created_at FROM book_shop.order_prod
ord_prod_seq = #{ord_prod_seq}
AND ord_seq = #{ord_seq}
AND ord_stat = #{ord_stat}
AND deli_stat = #{deli_stat}
AND pay_stat = #{pay_stat}
AND cust_id = #{cust_id}

mapper.delete:
DELETE
FROM book_shop.order_prod


        WHERE ord_seq = #{ord_seq}


        WHERE ord_prod_seq = #{ord_prod_seq}


        WHERE cust_id = #{cust_id}


        WHERE 1 = 2
