mapper.select:
SELECT cp.cart_seq, cp.isbn, cp.prod_type_code, cp.item_quan, book.book_title, book.papr_pric, book.e_pric, book.papr_point, book.e_point, bdh.papr_disc, bdh.e_disc, bi.img_url FROM book_shop.cart_prod cp INNER JOIN book_shop.book book ON cp.isbn = book.isbn INNER JOIN book_shop.book_disc_hist bdh ON cp.isbn = book.isbn AND book.isbn = bdh.isbn AND NOW() BETWEEN bdh.disc_start_date AND bdh.disc_end_date INNER JOIN book_shop.book_image bi ON cp.isbn = bi.isbn
cp.cart_seq = #{cart_seq}
AND cp.isbn = #{isbn}
AND cp.prod_type_code = #{prod_type_code}

mapper.delete:
DELETE
FROM
book_shop.cart_prod
WHERE
cart_seq = #{cart_seq}

    AND isbn = #{isbn}


    AND prod_type_code = #{prod_type_code}
